var UberQuick=function(){var e;var t=this;createButton=function(e){var t=e.button_text;var n=e.store_location;var r=e.base_url;var i="";var s="";var o="";for(var u=0;u<e.items.length;u++){o+="&name="+e.items[u].item_name;i+="&price="+e.items[u].price;s+="&quantity="+e.items[u].quantity}var a=r+"/checkout/location="+n+o+i+s;var t="<button action='"+a+"' class='uberquick-checkout'>"+t+"</button>";return t};sendRequest=function(){var e=$.ajax({url:$(".uberquick-checkout").attr("action"),type:"POST",dataType:"jsonp"});e.done(function(){console.log("success")});e.fail(function(){console.log("fail")})};return{loadTransaction:function(t){e=$(createButton(t))},getButton:function(){return e},bindEvent:function(){$("body").on("click",".uberquick-checkout",function(e){e.preventDefault();sendRequest()})}}}()